def get_iq_name(iq_score):
    if iq_score >= 164:
        return 0, 'نابغه سرآمد'
    elif 148 <= iq_score <= 163:
        return 1, 'نابغه'
    elif 132 <= iq_score <= 147:
        return 2, 'تیزهوش'
    elif 120 <= iq_score <= 131:
        return 3, 'باهوش'
    elif 110 <= iq_score <= 119:
        return 4, 'متوسط قوی'
    elif 90 <= iq_score <= 109:
        return 5, 'متوسط'
    elif 68 <= iq_score <= 89:
        return 6, 'مرزی'
    elif 52 <= iq_score <= 67:
        return 7, 'آموزش پذیر'
    elif 36 <= iq_score <= 51:
        return 8, 'تربیت پذیر'
    else:
        return 9, 'حمایت پذیر'


def catel_info(iq_score):
    iq_name = get_iq_name(iq_score)
    first_sentnces = 'همانطور که مشاهده کرده‌اید بهره هوشی شما در دسته افراد ' + iq_name[1] + ' قرار دارد.'
    if iq_score >= 132:
        return first_sentnces + catel_data[0][0], catel_data[0][1], iq_name[1], iq_name[0]
    if 120 <= iq_score <= 131:
        return first_sentnces + catel_data[1][0], catel_data[1][1], iq_name[1], iq_name[0]
    if 119 <= iq_score <= 110:
        return first_sentnces + catel_data[2][0], catel_data[2][1], iq_name[1], iq_name[0]
    if 109 <= iq_score <= 100:
        return first_sentnces + catel_data[3][0], catel_data[3][1], iq_name[1], iq_name[0]
    if 99 <= iq_score <= 85:
        return first_sentnces + catel_data[4][0], catel_data[4][1], iq_name[1], iq_name[0]
    else:
        return first_sentnces + catel_data[5][0], catel_data[5][1], iq_name[1], iq_name[0]


